import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Menu from "./pages/Menu";
import OrderSummary from "./pages/OrderSummary";
import ClientInfo from "./pages/ClientInfo";
import EndScreen from "./pages/EndScreen";
import Navbar from "./pages/Navbar";
import React, { useState, useEffect } from "react";

function App() {
  const [menu, setMenu] = useState([]);
  const [filteredishes, setFiltereDishes] = useState();

  useEffect(() => {
    fetchMenu();
  }, []);

  const fetchMenu = () => {
    fetch("http://127.0.0.1:8000/api/categories/")
      .then((res) => res.json())
      .then((data) => setMenu(data));
  };

  const filterDishesByCategory = (category_id) => {
    fetch(`http://127.0.0.1:8000/api/dishes/?category=${category_id}`)
      .then((res) => res.json())
      .then((data) => setFiltereDishes(data));
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Navbar />}>
          <Route index element={<Home />} />
          <Route
            path="menu/"
            element={
              <Menu
                categoryList={menu}
                filterDishesByCategory={filterDishesByCategory}
              />
            }
          />

          <Route path="summary/" element={<OrderSummary />} />
          <Route path="info/" element={<ClientInfo />} />
          <Route path="order/completed" element={<EndScreen />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
