import React from "react";
import { Outlet, useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

function Navbar() {


  function navToCategoryLink(category,categoryId) {
    return  <li className="nav-item">
      <Link className="nav-link active" to={`/category/${categoryId}`}>
            {category}
    </Link>
    </li>
  }

  const navbarStyle = {
    display:'flex',
    flexDirection:'row',
    width:'100%'
  }
  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <a className="navbar-brand" href="/">
            Noyni
          </a>
          <button className="navbar-toggler" type="button"></button>
          <div style = {navbarStyle}>
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" href="/menu">
                  All
                </a>
              </li>

                {navToCategoryLink("Burgers",1)}

                {navToCategoryLink("Others",3)}

                {navToCategoryLink("Sides",4)}

                {navToCategoryLink("Drinks",5)}

                {navToCategoryLink("Milkshakes",6)}

                <Link to={"/cart"} className="btn btn-primary">
              My cart
            </Link>
            </ul>
          </div>
          <nav className="navbar navbar-light bg-light">
            <div className="container-fluid">
              <form className="d-flex">
                <input
                  className="form-control me-2"
                  type="search"
                  placeholder="Search"
                  aria-label="Search"
                />
                <button className="btn btn-outline-success" type="submit">
                  Search
                </button>
              </form>
            </div>
          </nav>
        </div>
      </nav>
      <Outlet />
    </div>
  );
}

export default Navbar;
