import React, { useEffect, useState } from "react";
import CategoryCard from "../components/CategoryCard";
import { useParams } from "react-router-dom";

function Menu() {
  const [menu, setMenu] = useState([]);
  const [filteredishes, setFiltereDishes] = useState();

  const fetchMenu = () => {
    fetch("http://127.0.0.1:8000/api/categories/")
      .then((res) => res.json())
      .then((data) => setMenu(data));
  };

  const filterDishesByCategory = (category_id) => {
    fetch(`http://127.0.0.1:8000/api/dishes/?category=${category_id}`)
      .then((res) => res.json())
      .then((data) => {
        setFiltereDishes(data);
      });
  };

  let { id } = useParams();

  useEffect(() => {
    fetchMenu();
  }, []);

  return (
    <div className="container">
      {id == undefined ? 
      (menu.map((category) => (<CategoryCard key={category.name} category={category} /> ))) : 
      <Cards></Cards>
      }
    </div>
  );
}

export default Menu;
