import React from "react";
import { Link } from "react-router-dom";
import { useCart } from "../cartContext";

function ClientInfo() {


  const {cart}  = useCart()

  function submitOrder(e) {
    e.preventDefault();
    // make a call to server with the client info 
    let firstName = e.target[0].value
    let lastName = e.target[1].value
    let address = e.target[2].value
    let floor = e.target[3].value
    let phoneNumber = e.target[4].value

    let serverRequestObject = {firstName,lastName,address,floor,phoneNumber,cart}
    fetch('...',{body:serverRequestObject})
    .then(response => {
        // response contains order id
        // navigate to thank you screen
    }).catch(err => {
        // error
    })
  }
  return (
    <form style={{ width: "20%", margin: "17px" }} onSubmit={submitOrder}>
      <div className="form-group">
        <h5>Delivery Info</h5>
        <label htmlFor="exampleFormControlInput1">First name</label>
        <input type="text" className="form-control" />
        <label htmlFor="exampleFormControlInput1">Last name</label>
        <input type="text" className="form-control" />
        <label htmlFor="exampleFormControlInput1">Address</label>
        <input type="text" className="form-control" />
        <label htmlFor="exampleFormControlInput1">Floor</label>
        <input type="text" className="form-control" />
        <label htmlFor="exampleFormControlInput1">Phone Number</label>
        <input type="text" className="form-control" />
        <Link to={"/endscreen"} className="btn btn-primary" style={{marginTop:'10px'}}>
              Submit order
            </Link>
      </div>
    </form>
  );
}

export default ClientInfo;
