import React, { useEffect, useState } from "react";
import CategoryCard from "../components/CategoryCard";
import { useParams } from "react-router-dom";
import Cards from "./Cards";


function Menu() {
  const [menu, setMenu] = useState([]);
  const [filteredishes, setFiltereDishes] = useState();
  const [search, setSearch] = useState("");


  const fetchMenu = () => {
    fetch("http://127.0.0.1:8000/api/categories/")
      .then((res) => res.json())
      .then((data) => setMenu(data));
  };

  const filterDishesByCategory = (category_id) => {
    fetch(`http://127.0.0.1:8000/api/dish/?category=${category_id}`)
      .then((res) => res.json())
      .then((data) => {
        setFiltereDishes(data);
      });
  };

  const filterDishesBySearch = () => {
    fetch(`http://127.0.0.1:8000/movies?search=${search}`)
      .then((res) => res.json())
      .then((data) => setSearch(data));
  };






  let { id } = useParams();


  useEffect(() => {
    fetchMenu();
    if(id!==undefined)
    filterDishesByCategory(id)
  }, [id]);

  return (



    <div className="container">
      {id === false ? 
      (menu.map((category) => (<CategoryCard key={category.name} category={category} /> ))) : 

     (<Cards filteredishes={filteredishes}></Cards>)
      }
    </div>
  );
}

export default Menu;
