import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useParams } from 'react-router-dom';

export default function Cards() {


  const [dishes,setDishes] = React.useState()
  const { id } = useParams()
  const filterDishesByCategory = (category_id) => {
    fetch(`http://127.0.0.1:8000/api/dish/?category=${category_id}`)
      .then((res) => res.json())
      .then((data) => {
        if(data)
          setDishes(data)
        else
          setDishes(null)  
      }).catch(err => {
          setDishes(null)
      });
  };

  React.useEffect(() => {
    if(id)
       filterDishesByCategory(id)
  }, [id])

  return (
    <div>
           {
        dishes ? dishes.map(dish =>{
            return(
                <Card sx={{ maxWidth: 200, mb: 3.5,padding:2, maxHeight:390 }}>
            <CardMedia
              component="img"
              height="5%"
              width="10%"

              image={dish.imageUrl}
              alt="green iguana"
            />
            <CardContent>
              <Typography gutterBottom variant="h6" component="div">
                {dish.name}
              </Typography>
              <div>${dish.price}</div>
              <Typography variant="body2" color="text.secondary">
                {dish.description}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Add to cart</Button>
            </CardActions>
          </Card>
            )
        }) : dishes === null ?  <div>An unknown error has occured please try refreshing the page</div> : <div>Loading category dishes...</div>
    }
    </div>


  );
}
