  useEffect(() => {
    let total = 0
    for(let dish of cart)
        total += dish.price
  },[])
  return (
    <div style={{ marginTop: "25px", textAlign: "center" }}>
      <h4 style={{ color: "black" }}>Order summary</h4>
      <h5 style={{ margin: "17px", fontFamily: "Times New Roman" }}>
        total amount: ${" "}
      </h5>
      <button type="button" class="btn btn-primary">
        Proceed
      </button>
    </div>
  );
}

