import { Button } from "@mui/material";
import React from "react";
import {useState,useEffect} from "react";
import { useCart } from "../cartContext";
import { useNavigate } from "react-router-dom";

function ClientInfo({setOrder}) {

  let navigate = useNavigate();
  const {cart}  = useCart()
  const [cartId, setCartId] = useState([]);

  const createCartId = () =>
  {
    for (let i of cart) {
      setCartId((prevState)=> [...prevState,i.id])
    }
  }
  useEffect(() => {
    createCartId();
  }, []);

  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    address: "",
    phone:"",
    dishes:cartId
  });

  const handleForm = (e) => {
    setForm((formState) => {
      return {
        ...formState,
        [e.target.name]: e.target.value,
      };
    });
    console.log(form)
  };

  const addOrder = () => {

    fetch("http://127.0.0.1:8000/api/order/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then((data) => {
        setOrder(() => {
          return data;
        });
      });

    navigate("/endscreen", { replace: true });
  };

  return (

    <div className="container" style={{ padding: "20px" }}>
      <h5>Delivery Info</h5>
      <div>
        <input
          type="text"
          name="first_name"
          placeholder="First name"
          className="form-control"
          onChange={handleForm}
          value={form.first_name}
        />
      </div>
      <div>
        <input
          type="text"
          name="last_name"
          placeholder="Last name"
          className="form-control"
          onChange={handleForm}
          value={form.last_name}
        />
      </div>
      <div>
        <input
          type="text"
          name="address"
          placeholder="Address"
          className="form-control"
          onChange={handleForm}
          value={form.address}
        />
      </div>
      <div>
        <input
          type="number"
          name="phone"
          placeholder="Phone"
          className="form-control"
          onChange={handleForm}
          value={form.phone}
        />
      </div>

      <div>
      <button onClick={addOrder} className="btn btn-primary" style={{marginTop:'10px'}}>
              Submit order
      </button>
      </div>
    </div>
  );
}

export default ClientInfo;
