import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useParams } from 'react-router-dom';
import './Cards.css'
import { useCart } from '../cartContext';
import { Stack } from '@mui/material';
export default function Cards({propDishes}) {


  const {cart,setCart} = useCart()
  const [dishes,setDishes] = React.useState(propDishes)
  const { id } = useParams()

  const fetchMenu = () => {

    fetch("http://127.0.0.1:8000/api/dish/")
      .then((res) => res.json())
      .then((data) => {
        if(data) {
          if(data.category && data.category[0] === 'Select a valid choice. That choice is not one of the available choices.') {
              setDishes(null)
              alert(data.category[0])
          }else
              setDishes(data)   
       } else
        setDishes(null) 
      }).catch(err => {
        setDishes(null)
        console.log(err)
    });
  };

  const filterDishesByCategory = (category_id) => {
    fetch(`http://127.0.0.1:8000/api/dish/?category=${category_id}`)
      .then((res) => res.json())
      .then((data) => {
        if(data) {
            if(data.category && data.category[0] === 'Select a valid choice. That choice is not one of the available choices.') {
                setDishes(null)
                alert(data.category[0])
            }else
                setDishes(data)   
         } else
          setDishes(null)  
      }).catch(err => {
          setDishes(null)
          console.log(err)
      });
  };

  React.useEffect(() => {
    if(propDishes) return;
    if(id) {
      if(id != 'all')
       filterDishesByCategory(id)
      else { 
       fetchMenu()
      }
    }
  }, [id])




  return (
    <div className = 'grid_cards'>
           {dishes === null ?  <div>An unknown error has occured please try refreshing the page</div> :  dishes ?
            dishes.map(dish =>{
            return(
                <Card key={dish.id}
                 sx={{ maxWidth: 250, mb: 3.5,padding:2, maxHeight:420 }}>
            <CardMedia
              component="img"
              height="170px"
              width="10%"

              image={dish.imageUrl}
              alt="green iguana"
            />
            <CardContent>
              <Typography gutterBottom variant="h6" component="div">
                {dish.name}
              </Typography>
              <div>${dish.price}</div>
              <Typography variant="body2" height={'50px'} color="text.secondary">
                {dish.description}
              </Typography>
            </CardContent>
            <CardActions>
             <Stack>


             <Button size="small"
               sx ={{textTransform:'none'}}
              onClick ={() => {
                setCart([...cart,dish])
              }}>Add to cart</Button>
                 {propDishes && <Button size="small" 
                 sx ={{backgroundColor:'#bd3333',textTransform:'none'}} onClick ={() => {
                setCart([...cart,dish])
              }}>Remove from cart</Button>}
                  </Stack>
            </CardActions>
          </Card>
            )
        }) : <div>Loading category dishes...</div>
    }
    </div>


  );
}
